url_white_listed = ["https://github.com/" + _ for _ in
                    ["", "dragona", "topics", "features", "marketplace", "nonprofit", "collections", "events",
                     "customer-stories", "about", "trending", "pricing", "enterprise", "explore", "contact",
                     "security", "new", "issues", "notifications", "pulls"]]


def process(string_url, debug: bool = False) -> []:
    # TODO 2:
    # Update this function to
    # retrieve all github unique accounts
    # and do not include the white url_white_listed
    # in the list
    unique = list(set(string_url.split(",")))
    high_prob_user_link, other_links = (list(), list())
    for _ in unique:
        if "https://github.com/" in _ and len(_.split("/")) == 4 and len(
                _.split(".")) == 2 and "#" not in _ and "?" not in _:
            if _ in url_white_listed:
                continue
            high_prob_user_link.append(_)
        else:
            other_links.append(_)
    if debug:
        print(f'{len(high_prob_user_link)} high_prob_user_link')
        print(high_prob_user_link)
        print(f'{len(other_links)} other_links')
        print(other_links)
    return high_prob_user_link


def pretty_print(user_links: dict, full: bool = False):
    """
    :param user_links:
    :return:
    """
    # unique accounts from all
    all_links = []
    urls = []
    [all_links.extend(_) for _ in user_links.values()]
    [urls.append(_) for _ in user_links.values()]

    titles = [_ + " (" + str(len(url)) + ")" for _, url in zip(list(user_links.keys()), urls)]
    print('{0} {1}\t{2}\t{3}\t'.format(" " * 50, titles[0], titles[1], titles[2]))
    unique_urls = list(set(all_links))
    unique_urls.sort(reverse=False)
    counter_one, counter_tow, counter_three = (0, 0, 0)
    for url in unique_urls:
        if url in urls[0] and url in urls[1] and url in urls[2]:
            counter_one += 1
            print(
                '{0:<50} {1:>{2}}{3:<{4}}\t{5:>{6}}{7:<{8}}\t{9:>{10}}{11:<{12}}'.format(url, " ", len(titles[0]) // 2,
                                                                                         "*",
                                                                                         len(titles[0]) // 2, " ",
                                                                                         len(titles[1]) // 2, "*",
                                                                                         len(titles[1]) // 2, " ",
                                                                                         len(titles[2]) // 2, "*",
                                                                                         len(titles[2]) // 2))

        else:
            if full is False:
                continue
            var1, var2, var3 = (" " * 3)
            if url in urls[0]:
                var1 = "*"
            if url in urls[1]:
                var2 = "*"
            if url in urls[2]:
                var3 = "*"
            print(
                '{0:<50} {1:>{2}}{3:<{4}}\t{5:>{6}}{7:<{8}}\t{9:>{10}}{11:<{12}}'.format(url, " ", len(titles[0]) // 2,
                                                                                         var1,
                                                                                         len(titles[0]) // 2, " ",
                                                                                         len(titles[1]) // 2, var2,
                                                                                         len(titles[1]) // 2, " ",
                                                                                         len(titles[2]) // 2, var3,
                                                                                         len(titles[2]) // 2))
    if full is False:
        print("\nTotal (stargazers - watcher - forks): ", counter_one)


if __name__ == '__main__':
    user_links = {}
    keys = ["stargazers", "watcher", "forks"]
    for urls, key in zip([url_stargazers, url_watcher, url_forks], keys):
        user_links[key] = process(urls)
    pretty_print(user_links, full=False)
